// Function to format price in Indian currency
function formatPrice(price) {
    return new Intl.NumberFormat('en-IN', {
        style: 'currency',
        currency: 'INR',
        maximumFractionDigits: 0
    }).format(price);
}

// Function to create car card
function createCarCard(car) {
    return `
        <div class="car-card">
            <img src="${car.image}" alt="${car.name}">
            <div class="car-info">
                <h3>${car.name}</h3>
                <div class="specs">
                    <p><strong>Engine:</strong> ${car.specs.engine}</p>
                    <p><strong>Power:</strong> ${car.specs.power}</p>
                    <p><strong>Transmission:</strong> ${car.specs.transmission}</p>
                    <p><strong>Fuel Capacity:</strong> ${car.specs.fuelCapacity}</p>
                    <p><strong>Mileage:</strong> ${car.specs.mileage}</p>
                    <p><strong>Seating:</strong> ${car.specs.seating}</p>
                </div>
                <div class="price">${formatPrice(car.price)}</div>
            </div>
        </div>
    `;
}

// Function to filter cars
function filterCars() {
    const typeFilter = document.getElementById('typeFilter').value;
    const priceFilter = document.getElementById('priceFilter').value;
    const searchTerm = document.getElementById('searchInput').value.toLowerCase();

    return cars.filter(car => {
        const matchesType = typeFilter === 'all' || car.type === typeFilter;
        const matchesSearch = car.name.toLowerCase().includes(searchTerm);
        
        let matchesPrice = true;
        if (priceFilter !== 'all') {
            const [min, max] = priceFilter.split('-').map(Number);
            if (max) {
                matchesPrice = car.price >= min && car.price <= max;
            } else {
                matchesPrice = car.price >= min;
            }
        }

        return matchesType && matchesPrice && matchesSearch;
    });
}

// Function to update car display
function updateCarDisplay() {
    const filteredCars = filterCars();
    const carGrid = document.getElementById('carGrid');
    carGrid.innerHTML = filteredCars.map(car => createCarCard(car)).join('');
}

// Event listeners
document.getElementById('typeFilter').addEventListener('change', updateCarDisplay);
document.getElementById('priceFilter').addEventListener('change', updateCarDisplay);
document.getElementById('searchInput').addEventListener('input', updateCarDisplay);

// Initial display
document.addEventListener('DOMContentLoaded', updateCarDisplay);